import React from "react";

function DurationManager({focusDuration, setFocusDuration, breakDuration, setBreakDuration, isTimerRunning}){

  function decFocus() {
    if(focusDuration > 5) {
      setFocusDuration(focusDuration - 5);
    } 
  }

  function incFocus() {
    if(focusDuration < 60) {
      setFocusDuration(focusDuration + 5);
    } 
  }

  function decBreak() {
    if(breakDuration > 1) {
      setFocusDuration(focusDuration - 1);
    } 
  }

  function incBreak() {
    if(focusDuration < 15) {
      setFocusDuration(focusDuration + 1);
    } 
  }

    return(
        <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {("0" + focusDuration).substr(-2)}:00
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a 
              focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={() => {decFocus()}}
                disabled={isTimerRunning}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  
              and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={() => {incFocus()}}
                disabled={isTimerRunning}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {("0" + breakDuration).substr(-2)}:00
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and 
                disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={() => {if (breakDuration > 1) setBreakDuration(breakDuration - 1)}}
                  disabled={isTimerRunning}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration 
                and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={() => {if(breakDuration < 15) setBreakDuration(breakDuration + 1)}}
                  disabled={isTimerRunning}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
}

export default DurationManager;
